        jTextArea3.setText("public static void SommaEMedia(){  //es 10\n    Scanner input = new Scanner(System.in);\n    System.out.print(\"Inserisci il numero di dati da leggere: \");\n     int n = input.nextInt(); // legge il numero di dati da leggere\n     int[] numeri = new int[n]; // crea un array di lunghezza n per i dati\n     System.out.println(\"Inserisci i dati:\");\n     int somma = 0;\n     for (int i = 0; i < n; i++) {\n          numeri[i] = input.nextInt(); // legge i dati uno alla volta\n          somma = somma + numeri[i]; // aggiunge il valore alla somma\n      }\n      double media = somma / n; // calcola la media\n      System.out.println(\"La somma dei dati Ã¨: \" + somma);\n      System.out.println(\"La media dei dati Ã¨: \" + media);\n }");
        jScrollPane3.setViewportView(jTextArea3);

        jTextArea4.setColumns(20);
        jTextArea4.setRows(5);
        jTextArea4.setText("public class Main {\n    public static void main(String[] args) {\n        SommaEMedia SM = new SommaEMedia();\n        SM.SommaEMedia();\n    }\n}");
        jScrollPane4.setViewportView(jTextArea4);

        jTextArea5.setBackground(new java.awt.Color(204, 204, 204));
        jTextArea5.setColumns(20);
        jTextArea5.setRows(5);
        jTextArea5.setText("Il metodo legge un numero n di dati da input e crea un array\n di interi di lunghezza n per memorizzare i dati.\nSuccessivamente, richiede all'utente di inserire i dati uno alla \nvolta e li memorizza nell'array. \nDurante questo processo, il metodo aggiunge il valore di ogni \nelemento alla variabile somma.\nDopo aver memorizzato tutti i dati, il metodo calcola la media \ndei dati dividento la somma totale per il numero di dati. \nLa media viene memorizzata nella variabile media.\nInfine, il metodo stampa la somma dei dati e la media dei dati \nsulla console utilizzando il metodo println() della classe System.");
        jScrollPane5.setViewportView(jTextArea5);

