        jTextArea9.setText("public static void SommaPrimiDieciNumeri(){\n    Scanner input = new Scanner(System.in);\n    boolean inputValido = false;\n    while (!inputValido) {\n        try {\n            System.out.print(\"Inserisci un numero: \");\n            int n = input.nextInt(); // legge un numero da input\n            int somma = 0;\n            for (int i = n + 1; i <= n + 10; i++) {\n                somma = somma + i; // aggiunge il numero alla somma\n            }\n            System.out.println(\"La somma dei primi 10 numeri successivi a \" + n + \" Ã¨: \" + somma);\n            inputValido = true;\n        } catch (InputMismatchException e) {\n            System.out.println(\"Errore, inserire un numero intero.\");\n            input.nextLine(); // pulisce il buffer dell'input\n        }\n    }\n}");
        jScrollPane9.setViewportView(jTextArea9);

        jTextArea10.setColumns(20);
        jTextArea10.setRows(5);
        jTextArea10.setText("public class Main {\n    public static void main(String[] args) {\n        SommaPrimiDieciNumeri SD \n        = new SommaPrimiDieciNumeri();\n        SD.SommaPrimiDieciNumeri(); \n    }\n}");
        jScrollPane10.setViewportView(jTextArea10);

        jLabel5.setFont(new java.awt.Font("Segoe UI", 0, 18)); // NOI18N
        jLabel5.setText("ESERCIZIO SommaPrimiDieciNumeri");

        jTextArea25.setBackground(new java.awt.Color(204, 204, 204));
        jTextArea25.setColumns(20);
        jTextArea25.setRows(5);
        jTextArea25.setText("Il metodo SommaPrimiDieciNumeri() richiede \nall'utente di inserire un numero intero da input, \nsuccessivamente, il metodo calcola la somma dei \nprimi 10 numeri successivi al numero inserito \nutilizzando un ciclo for.\nSe l'utente inserisce un valore non intero, il metodo\ngestisce l'eccezione di tipo InputMismatchException \ne richiede all'utente di inserire nuovamente il numero.\nAlla fine il metodo stampa il risultato della somma\nsulla console utilizzando il metodo println().");
        jScrollPane25.setViewportView(jTextArea25);

