        jTextArea11.setText("    public static void MediaAritmetica(){ \n        Scanner input = new Scanner(System.in);\n        System.out.print(\"Inserisci la quantitÃ  di numeri da leggere: \");\n        int n = input.nextInt(); // legge il numero di numeri da leggere\n        double somma = 0;\n        for (int i = 1; i <= n; i++) {\n            System.out.print(\"Inserisci il \" + i + \"Â° numero: \");\n            double numero = input.nextDouble(); // legge il numero da input\n            somma = somma + numero; // aggiunge il numero alla somma\n        }\n        double media = somma / n; // calcolo della media\n        System.out.println(\"La media dei numeri inseriti Ã¨: \" + media);\n    }");
        jScrollPane11.setViewportView(jTextArea11);

