        jTextArea14.setText("public static void DivisoriDiN() {\n    Scanner input = new Scanner(System.in);\n    boolean inputValido = false;\n    do {\n        try {\n            System.out.print(\"Inserisci un numero: \");\n            int n = input.nextInt(); // legge un numero da input\n            System.out.println(\"I divisori di \" + n + \" sono:\");\n            for (int i = 1; i <= n; i++) { // utilizzo un for per creare tutti i numeri compresi tra 1 e n.\n                if (n % i == 0) {      //controllo se tutti numeri precenedeti, compreso n, sono dei divisori traminte il modulo \n                    System.out.println(i);\n                }\n            }\n            inputValido = true;\n        } catch (InputMismatchException e) {\n            System.out.println(\"L'input inserito non Ã¨ valido. Riprova.\");\n            input.nextLine(); // pulisce il buffer dell'input\n        }\n    } while (!inputValido);\n}");
        jScrollPane14.setViewportView(jTextArea14);

        jTextArea15.setColumns(20);
        jTextArea15.setRows(5);
        jTextArea15.setText("public class Main {\n    public static void main(String[] args) {\n        DivisoriDiN DN= new DivisoriDiN();\n        DN.DivisoriDiN(); \n    }\n}");
        jScrollPane15.setViewportView(jTextArea15);

