        jTextArea16.setText("public static void SommaNumeriNaturali(){\n   Scanner input = new Scanner(System.in);\n   int somma = 0;\n   int numero;\n   do {\n        System.out.print(\"Inserisci un numero naturale, se voui terminare il programma inserisci un numero negativo: \");\n        numero = input.nextInt(); // legge un numero da input\n        if (numero >= 0) {\n           somma = somma + numero; // aggiunge il numero alla somma solo se Ã¨ positivo\n        }\n    } while (numero >= 0); // continua finchÃ© non viene inserito un numero negativo\n    System.out.println(\"La somma dei numeri inseriti Ã¨: \" + somma);\n}");
        jScrollPane16.setViewportView(jTextArea16);

